import React, { useState, useRef, useEffect } from "react";
import axios from "axios";
import "../styles.css";

const incomeSources = [
  "Job", "Side-hustle", "Family support", "Scholarships", "Reimbursements", "Tax refunds"
];
const expenseCategories = [
  "Rent", "Groceries", "Utilities", "Transport", "Tuition", "Dining out", "Entertainment", "Other"
];

export default function ChatWindow() {
  const [theme, setTheme] = useState("light");
  const [fontSize, setFontSize] = useState(1.0);
  const [step, setStep] = useState(0);
  const [userState, setUserState] = useState({
    name: "", goal: "", income: {}, expenses: {}, qna: "", insights: ""
  });
  const [input, setInput] = useState("");
  const [loading, setLoading] = useState(false);
  const [chatResponse, setChatResponse] = useState("");
  const [spreadsheetUrl, setSpreadsheetUrl] = useState("");

  const inputRef = useRef(null);
  useEffect(() => {
    if (inputRef.current) inputRef.current.focus();
  }, [step, loading, chatResponse]);

  // Debug print for state
  useEffect(() => {
    console.log("游릭 Frontend state:", { step, userState });
  }, [step, userState]);

  const handleNext = (field) => {
    setUserState(s => ({ ...s, [field]: input }));
    setInput("");
    setStep(step + 1);
  };

  const handleMultiNext = (field, key, value) => {
    setUserState(s => ({
      ...s,
      [field]: { ...s[field], [key]: value }
    }));
    setInput("");
    setStep(step + 1);
  };

  const sendLLM = async (stepName) => {
    setLoading(true);
    try {
      const res = await axios.post("http://localhost:8000/chat", {
        step: stepName,
        user_state: userState,
      });
      console.log("游릭 Frontend got:", res.data);
      setChatResponse(res.data.response);
      setUserState(s => ({
        ...s,
        last_bot_message: res.data.response,
        insight_text: stepName === "insights" ? res.data.response : s.insight_text
      }));
    } catch (err) {
      console.error(err);
      setChatResponse("Oops! Something went wrong.");
    }
    setLoading(false);
  };

  const download = async () => {
    setLoading(true);
    try {
      const res = await axios.post("http://localhost:8000/export-budget", userState, { responseType: "blob" });
      const url = URL.createObjectURL(new Blob([res.data]));
      setSpreadsheetUrl(url);
      console.log("游릭 Frontend downloaded spreadsheet");
    } catch (err) {
      console.error(err);
    }
    setLoading(false);
  };

  // Font size control
  const fontSizePct = Math.round(fontSize * 100);

  return (
    <>
      <div className="theme-toggle-row">
        <button
          aria-label={`Switch to ${theme === "dark" ? "light" : "dark"} mode`}
          className="theme-toggle-btn"
          tabIndex={0}
          onClick={() => setTheme(theme === "dark" ? "light" : "dark")}
        >
          {theme === "dark" ? "游 Light Mode" : "游깿 Dark Mode"}
        </button>
        <button
          aria-label={`Increase font size (current: ${fontSizePct}%)`}
          className="font-size-btn"
          tabIndex={0}
          style={{ marginLeft: 8 }}
          onClick={() => setFontSize(f => Math.min(f + 0.15, 1.7))}
        >A+</button>
        <button
          aria-label={`Decrease font size (current: ${fontSizePct}%)`}
          className="font-size-btn"
          tabIndex={0}
          style={{ marginLeft: 2 }}
          onClick={() => setFontSize(f => Math.max(f - 0.15, 0.7))}
        >A-</button>
      </div>
      <div className={`chat-window-modern ${theme}`} style={{ fontSize: `${fontSize}em` }}>
        {/* Step 0: Greeting */}
        {step === 0 && (
          <div className="bot-bubble bubble-animate" role="status" aria-live="polite">
            <h2>Hello, I am FinBot, your AI Budgeting Assistant.</h2>
            <button
              className="bubble-action-btn"
              tabIndex={0}
              aria-label="Start"
              onClick={() => setStep(1)}
            >Start</button>
          </div>
        )}
        {/* Step 1: Name */}
        {step === 1 && (
          <div className="bot-bubble bubble-animate" role="status" aria-live="polite">
            <label htmlFor="name-input">What is your name?</label>
            <input
              ref={inputRef}
              id="name-input"
              value={input}
              onChange={e => setInput(e.target.value)}
              onKeyDown={e => e.key === "Enter" && handleNext("name")}
              tabIndex={0}
              aria-label="Your name"
            />
            <button
              className="bubble-action-btn"
              tabIndex={0}
              aria-label="Next"
              onClick={() => handleNext("name")}
            >Next</button>
          </div>
        )}
        {/* Step 2: Financial Goal */}
        {step === 2 && (
          <div className="bot-bubble bubble-animate" role="status" aria-live="polite">
            <label htmlFor="goal-input">What is your financial goal this month?</label>
            <input
              ref={inputRef}
              id="goal-input"
              value={input}
              onChange={e => setInput(e.target.value)}
              onKeyDown={e => e.key === "Enter" && handleNext("goal")}
              tabIndex={0}
              aria-label="Your financial goal"
            />
            <button
              className="bubble-action-btn"
              tabIndex={0}
              aria-label="Next"
              onClick={() => handleNext("goal")}
            >Next</button>
          </div>
        )}
        {/* Step 3: Confirmation */}
        {step === 3 && (
          <div className="bot-bubble bubble-animate" role="status" aria-live="polite">
            <p id="goal-thought-prompt">You wanna know what I think about your financial goal?</p>
            <button className="bubble-action-btn" tabIndex={0} aria-label="Yes" onClick={() => setStep(step + 1)}>Yes</button>
            <button className="bubble-action-btn" tabIndex={0} aria-label="No" onClick={() => setStep(step + 1)}>No</button>
          </div>
        )}
        {/* Income collection */}
        {step >= 4 && step < 4 + incomeSources.length && (() => {
          const idx = step - 4;
          const label = incomeSources[idx];
          return (
            <div className="bot-bubble bubble-animate" role="status" aria-live="polite">
              <label htmlFor={`income-${label}`}>How much do you get from {label}?</label>
              <input
                ref={inputRef}
                id={`income-${label}`}
                type="number"
                value={input}
                onChange={e => setInput(e.target.value)}
                onKeyDown={e => e.key === "Enter" && input && handleMultiNext("income", label, Number(input))}
                tabIndex={0}
                aria-label={`Income from ${label}`}
              />
              <button
                className="bubble-action-btn"
                tabIndex={0}
                aria-label="Next"
                onClick={() => input && handleMultiNext("income", label, Number(input))}
                disabled={!input}
              >Next</button>
            </div>
          );
        })()}
        {/* Expenses collection */}
        {step >= 4 + incomeSources.length && step < 4 + incomeSources.length + expenseCategories.length && (() => {
          const idx = step - (4 + incomeSources.length);
          const label = expenseCategories[idx];
          return (
            <div className="bot-bubble bubble-animate" role="status" aria-live="polite">
              <label htmlFor={`expense-${label}`}>How much do you spend on {label}?</label>
              <input
                ref={inputRef}
                id={`expense-${label}`}
                type="number"
                value={input}
                onChange={e => setInput(e.target.value)}
                onKeyDown={e => e.key === "Enter" && input && handleMultiNext("expenses", label, Number(input))}
                tabIndex={0}
                aria-label={`Expenses for ${label}`}
              />
              <button
                className="bubble-action-btn"
                tabIndex={0}
                aria-label="Next"
                onClick={() => input && handleMultiNext("expenses", label, Number(input))}
                disabled={!input}
              >Next</button>
            </div>
          );
        })()}
        {/* Insights */}
        {step === 4 + incomeSources.length + expenseCategories.length && (
          <div className="bot-bubble bubble-animate" role="status" aria-live="polite">
            <p>Insights for optimizing your money management skills</p>
            <button className="bubble-action-btn" tabIndex={0} aria-label="Get Insights" onClick={() => sendLLM("insights")} disabled={loading}>
              {loading ? "Thinking..." : "Get Insights"}
            </button>
            {chatResponse && <div className="insights">{chatResponse}</div>}
            {chatResponse && <button className="bubble-action-btn" tabIndex={0} aria-label="Next" onClick={() => setStep(step + 1)}>Next</button>}
          </div>
        )}
        {/* Q&A */}
        {step === 5 + incomeSources.length + expenseCategories.length && (
          <div className="bot-bubble bubble-animate" role="status" aria-live="polite">
            <label htmlFor="qna-input">Do you have any questions about your money management or budgeting?</label>
            <input
              ref={inputRef}
              id="qna-input"
              value={userState.qna}
              onChange={e => setUserState(s => ({ ...s, qna: e.target.value }))}
              onKeyDown={e => e.key === "Enter" && sendLLM("qna")}
              tabIndex={0}
              aria-label="Money management question"
            />
            <button className="bubble-action-btn" tabIndex={0} aria-label="Ask" onClick={() => sendLLM("qna")} disabled={loading}>
              {loading ? "Thinking..." : "Ask"}
            </button>
            {chatResponse && <div className="insights">{chatResponse}</div>}
            <button className="bubble-action-btn" tabIndex={0} aria-label="Next" onClick={() => setStep(step + 1)}>Next</button>
          </div>
        )}
        {/* Spreadsheet download */}
        {step === 6 + incomeSources.length + expenseCategories.length && (
          <div className="bot-bubble bubble-animate" role="status" aria-live="polite">
            <p>Download your budget template as a spreadsheet</p>
            <button className="bubble-action-btn" tabIndex={0} aria-label="Download Spreadsheet" onClick={download} disabled={loading}>Download</button>
            {spreadsheetUrl && (
              <a href={spreadsheetUrl} download="budget.xlsx">Click here if your download does not start.</a>
            )}
          </div>
        )}
        {/* Done */}
        {step > 6 + incomeSources.length + expenseCategories.length && (
          <div className="bot-bubble bubble-animate" role="status" aria-live="polite"><p>Thank you for using FinBot! Refresh to start over.</p></div>
        )}
      </div>
    </>
  );
}
