import React, { useState, useRef, useEffect } from "react";
import axios from "axios";
import "../styles.css";

const incomeSources = [
  "Job", "Side-hustle", "Family support", "Scholarships", "Reimbursements", "Tax refunds"
];
const expenseCategories = [
  "Rent", "Groceries", "Utilities", "Transport", "Tuition", "Dining out", "Entertainment", "Other"
];

export default function ChatWindow() {
  const [theme, setTheme] = useState("light");
  const [fontSize, setFontSize] = useState(1.0);
  const [step, setStep] = useState(0);
  const [userState, setUserState] = useState({
    name: "", goal: "", income: {}, expenses: {}, qna: "", insights: ""
  });
  const [input, setInput] = useState("");
  const [loading, setLoading] = useState(false);
  const [chatResponse, setChatResponse] = useState("");
  const [spreadsheetUrl, setSpreadsheetUrl] = useState("");
  const [messages, setMessages] = useState([
    { from: "bot", text: "Hello, I am FinBot, your AI Budgeting Assistant. What is your name?" }
  ]);

  const inputRef = useRef(null);
  const chatEndRef = useRef(null);

  // Auto-focus and auto-scroll
  useEffect(() => {
    if (inputRef.current) inputRef.current.focus();
  }, [step, loading, chatResponse]);
  useEffect(() => {
    if (chatEndRef.current) chatEndRef.current.scrollIntoView({ behavior: "smooth" });
  }, [messages, loading]);

  // Debug print for state
  useEffect(() => {
    console.log("ðŸŸ¢ Frontend state:", { step, userState });
  }, [step, userState]);

  // Restart/Clear Chat
  const handleRestart = () => {
    setMessages([{ from: "bot", text: "Hello, I am FinBot, your AI Budgeting Assistant. What is your name?" }]);
    setStep(1);
    setUserState({
      name: "", goal: "", income: {}, expenses: {}, qna: "", insights: ""
    });
    setInput("");
    setChatResponse("");
    setSpreadsheetUrl("");
  };

  // Stepper logic + message array
  const addMessage = (from, text) => {
    setMessages(prev => [...prev, { from, text }]);
  };

  const handleUserInput = async () => {
    if (!input.trim() || loading) return;
    if (step === 1) {
      addMessage("user", input);
      setUserState(s => ({ ...s, name: input }));
      setInput("");
      setTimeout(() => {
        addMessage("bot", "What is your financial goal this month?");
        setStep(2);
      }, 400);
    } else if (step === 2) {
      addMessage("user", input);
      setUserState(s => ({ ...s, goal: input }));
      setInput("");
      setTimeout(() => {
        addMessage("bot", "You wanna know what I think about your financial goal?");
        setStep(3);
      }, 400);
    } else if (step === 3) {
      addMessage("user", input || "Yes");
      setInput("");
      setTimeout(() => {
        addMessage("bot", `How much do you get from ${incomeSources[0]}?`);
        setStep(4);
      }, 400);
    } else if (step >= 4 && step < 4 + incomeSources.length) {
      const idx = step - 4;
      addMessage("user", `$${input} from ${incomeSources[idx]}`);
      setUserState(s => ({
        ...s,
        income: { ...s.income, [incomeSources[idx]]: Number(input) }
      }));
      setInput("");
      if (step + 1 < 4 + incomeSources.length) {
        setTimeout(() => {
          addMessage("bot", `How much do you get from ${incomeSources[idx + 1]}?`);
          setStep(step + 1);
        }, 400);
      } else {
        setTimeout(() => {
          addMessage("bot", `How much do you spend on ${expenseCategories[0]}?`);
          setStep(step + 1);
        }, 400);
      }
    } else if (
      step >= 4 + incomeSources.length &&
      step < 4 + incomeSources.length + expenseCategories.length
    ) {
      const idx = step - (4 + incomeSources.length);
      addMessage("user", `$${input} for ${expenseCategories[idx]}`);
      setUserState(s => ({
        ...s,
        expenses: { ...s.expenses, [expenseCategories[idx]]: Number(input) }
      }));
      setInput("");
      if (step + 1 < 4 + incomeSources.length + expenseCategories.length) {
        setTimeout(() => {
          addMessage("bot", `How much do you spend on ${expenseCategories[idx + 1]}?`);
          setStep(step + 1);
        }, 400);
      } else {
        setTimeout(() => {
          addMessage("bot", `Insights for optimizing your money management skills`);
          setStep(step + 1);
        }, 400);
      }
    } else if (step === 4 + incomeSources.length + expenseCategories.length) {
      addMessage("user", "Show me insights");
      setLoading(true);
      addMessage("bot", <span className="typing-indicator">FinBot is typingâ€¦</span>);
      const res = await axios.post("http://localhost:8000/chat", {
        step: "insights",
        user_state: userState,
      });
      setTimeout(() => {
        setMessages(prev => [
          ...prev.slice(0, prev.length - 1),
          { from: "bot", text: res.data.response }
        ]);
        setUserState(s => ({
          ...s,
          insights: res.data.response
        }));
        setLoading(false);
        setInput("");
        setStep(step + 1);
      }, 600);
    } else if (step === 5 + incomeSources.length + expenseCategories.length) {
      addMessage("user", input);
      setUserState(s => ({ ...s, qna: input }));
      setLoading(true);
      addMessage("bot", <span className="typing-indicator">FinBot is typingâ€¦</span>);
      const res = await axios.post("http://localhost:8000/chat", {
        step: "qna",
        user_state: { ...userState, qna: input }
      });
      setTimeout(() => {
        setMessages(prev => [
          ...prev.slice(0, prev.length - 1),
          { from: "bot", text: res.data.response }
        ]);
        setLoading(false);
        setInput("");
        setStep(step + 1);
      }, 600);
    }
  };

  // For Q&A step, on Enter also send (so user doesn't have to click Ask)
  const isQnAStep = step === 5 + incomeSources.length + expenseCategories.length;

  // Last step: download
  const download = async () => {
    setLoading(true);
    try {
      const res = await axios.post("http://localhost:8000/export-budget", userState, { responseType: "blob" });
      const url = URL.createObjectURL(new Blob([res.data]));
      setSpreadsheetUrl(url);
      addMessage("bot", "Your budget spreadsheet is ready!");
    } catch (err) {
      addMessage("bot", "Sorry, could not download spreadsheet.");
    }
    setLoading(false);
  };

  // Font size control
  const fontSizePct = Math.round(fontSize * 100);

  return (
    <>
      <div className="theme-toggle-row">
        <button
          aria-label={`Switch to ${theme === "dark" ? "light" : "dark"} mode`}
          className="theme-toggle-btn"
          tabIndex={0}
          onClick={() => setTheme(theme === "dark" ? "light" : "dark")}
        >
          {theme === "dark" ? "ðŸŒž Light Mode" : "ðŸŒ™ Dark Mode"}
        </button>
        <button
          aria-label={`Increase font size (current: ${fontSizePct}%)`}
          className="font-size-btn"
          tabIndex={0}
          style={{ marginLeft: 8 }}
          onClick={() => setFontSize(f => Math.min(f + 0.15, 1.7))}
        >A+</button>
        <button
          aria-label={`Decrease font size (current: ${fontSizePct}%)`}
          className="font-size-btn"
          tabIndex={0}
          style={{ marginLeft: 2 }}
          onClick={() => setFontSize(f => Math.max(f - 0.15, 0.7))}
        >A-</button>
        <button
          className="restart-btn"
          tabIndex={0}
          aria-label="Restart conversation"
          style={{ marginLeft: 16 }}
          onClick={handleRestart}
        >Restart</button>
      </div>
      <div className={`chat-window-modern ${theme}`} style={{ fontSize: `${fontSize}em` }}>
        <div className="chat-scrollbox">
          {messages.map((msg, i) => (
            <div
              key={i}
              className={`bubble-animate ${msg.from === "user" ? "user-bubble" : "bot-bubble"}`}
              role="status"
              aria-live="polite"
              tabIndex={0}
            >
              {msg.text}
            </div>
          ))}
          <div ref={chatEndRef} />
        </div>
        {(step <= 5 + incomeSources.length + expenseCategories.length) && (
          <div style={{ display: "flex", gap: 10 }}>
            <input
              ref={inputRef}
              value={input}
              onChange={e => setInput(e.target.value)}
              onKeyDown={e => e.key === "Enter" && handleUserInput()}
              aria-label="Your answer"
              style={{ flex: 1, minWidth: 0 }}
              disabled={loading}
              tabIndex={0}
            />
            <button
              className="bubble-action-btn"
              onClick={handleUserInput}
              disabled={loading || !input.trim()}
              aria-label="Send"
              tabIndex={0}
            >
              Send
            </button>
          </div>
        )}
        {/* Spreadsheet download step */}
        {step > 5 + incomeSources.length + expenseCategories.length && (
          <div style={{ marginTop: 16 }}>
            <button className="bubble-action-btn" onClick={download} disabled={loading}>
              Download Budget Spreadsheet
            </button>
            {spreadsheetUrl && (
              <a href={spreadsheetUrl} download="budget.xlsx" style={{ marginLeft: 16 }}>
                Click here if your download does not start.
              </a>
            )}
          </div>
        )}
      </div>
    </>
  );
}
